# create session
curl --location --request POST 'localhost:8080/sessions' --header 'mainzellisteApiKey: pleaseChangeMeToo' --header 'mainzellisteApiVersion: 3.2'

# replace {session-id} and create addConsentPolicySet token
curl --location 'localhost:8080/sessions/{session-id}/tokens' --header 'mainzellisteApiKey: pleaseChangeMeToo' --header 'Content-Type: application/json' --header 'mainzellisteApiVersion: 3.2' --data '{"type": "addConsentPolicySet"}'

# replace {policy-set-token-id} and create policy set
curl --location 'localhost:8080/consent-policies' --header 'mainzellisteApiKey: pleaseChangeMeToo' --header 'Content-Type: application/json' --header 'Accept: application/json' --header 'Authorization: MainzellisteToken {policy-set-token-id}' --data '{"id": "urn:oid:2.16.840.1.113883.3.1937.777.24.5.3", "name": "Mii Consent Policy - 1.0.2"}'

# replace {session-id} and create addConsentPolicy token
